import io from 'socket.io-client'
import './App.css';
import {useEffect,useState} from "react";

const socket = io.connect("http://localhost:3001")
function App() {
  const [message,setMessage] = useState("");
  const [reason,setReason] = useState("");
  const [messageReceived, setMessageReceived] = useState("");
  const [lat,setLat] = useState(0);
  const [long,setLong] = useState(0);

  const compileMessage = () => {
    setMessage(`${lat}${long}${message}`);
    sendMessage();
  };

  const sendMessage = () => {
    socket.emit('send_message', {message});
  };


  useEffect(() => {
    navigator.geolocation.getCurrentPosition((postion) => {
      setLat(postion.coords.latitude)
      setLong(postion.coords.latitude)
    })
    socket.on("received_message", (data) => {
      setMessageReceived(data.message);
    })
  },[socket])

  return (
    <div className="App">
      <input placeholder='Message...' onChange={(event) => { setReason(event.target.value);}}/>
      <button onClick={compileMessage}>Send Message</button>
      <h1> Message:</h1> {messageReceived}
    </div>
  );
}

export default App;
